package fullStackJavaCourse;

public class multiplyGivenNumberRunner
{
	public static void multiply(int num) 
	{
		for(int i=1;i<=10;i++)
		{
		System.out.printf("%d * %d = %d", num,i,num*i).println();
		}
	}

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		multiply(5);
		

	}

}
